function getPage() {
    var dlPage = google_tag_manager[{{Container ID}}].dataLayer.get('page')
    var path = document.location.pathname
    var checkoutPath = {{JS - Checkout Page}}
    switch(checkoutPath) {
      case "thank_you":
      checkoutPath = "/checkout/thank_you";
      break;
    case "processing":
      checkoutPath = "/checkout/processing";
      break;
    case "shipping_method":
      checkoutPath = "/checkout/shipping";
      break;
    case "contact_information":
      checkoutPath = "/checkout/contact_information";
      break;
    case "payment_method":
      checkoutPath = "/checkout/payment";
    }
    if (path.includes('checkouts')) {
        var page = checkoutPath
    } else if (isNaN(dlPage)) {
        var page = path
    } else {
        var page = dlPage
    }
    return page
}

function() {
    // Get local time as ISO string with offset at the end
    var now = new Date();
    var tzo = -now.getTimezoneOffset();
    var dif = tzo >= 0 ? '+' : '-';
    var pad = function(num) {
        var norm = Math.abs(Math.floor(num));
        return (norm < 10 ? '0' : '') + norm;
    };
    return now.getFullYear() 
        + '-' + pad(now.getMonth()+1)
        + '-' + pad(now.getDate())
        + 'T' + pad(now.getHours())
        + ':' + pad(now.getMinutes()) 
        + ':' + pad(now.getSeconds())
        + '.' + pad(now.getMilliseconds())
        + dif + pad(tzo / 60) 
        + ':' + pad(tzo % 60);
}

